mapper.select:
SELECT i.id,i.itemName,i.endTime,i.startTime,i.minPrice,c.areaId 'dmCinema.areaId',c.areaName 'dmCinema.areaName',
 c.address 'dmCinema.address'
FROM  dm_item i,dm_cinema c WHERE i.cinemaId=c.id AND  i.itemType1Id = #{itemTypeId} AND i.startTime > #{start} AND i.endTime < #{end}

SELECT d.id,d.itemName,d.startTime,d.endTime,
        d.minPrice,c.areaId,c.areaName,c.address
FROM dm_item d,dm_cinema c
WHERE c.id=d.cinemaId AND d.`itemType1Id`=#{itemTypeId}
LIMIT #{limit}
